mapper.select:
SELECT memlvno, memlv_name, memlv_img, memlv_condition ,memlv_benefit
FROM memlv
WHERE memlvno=#{memlvno}

mapper.update:
UPDATE memlv
SET memlv_name=#{memlv_name}, memlv_condition=#{memlv_condition} , memlv_benefit=#{memlv_benefit}
WHERE memlvno = #{memlvno}

UPDATE memlv
SET memlv_img=#{memlv_img}
WHERE memlvno=#{memlvno}
